import React, { createContext, useState } from "react";
import { v1 as uuidv1 } from "uuid";

export const WordContext = createContext();

const WordContextProvider = props => {
  const [words, setWords] = useState([
    { word: "a", definition: "abc", id: uuidv1() }
    //{ word: "b", definition: "abc", id: uuidv1() }
  ]);

  const addWord = (word, definition) => {
    setWords([...words, { word, definition, id: uuidv1() }]);
  };

  const removeWord = id => {
    setWords(words.filter(word => word.id !== id));
  };

  return (
    <WordContext.Provider value={{ words, addWord, removeWord }}>
      {props.children}
    </WordContext.Provider>
  );
};

export default WordContextProvider;
